// Find the To field and fill it
const toField = document.querySelector('input[aria-label="To"]');
if (toField) {
  toField.value = 'namor5772@gmail.com';
  toField.dispatchEvent(new Event('input', { bubbles: true }));
  return 'To field filled';
}
return 'To field not found';

// Find all input fields in the compose window
const inputs = document.querySelectorAll('input, textarea');
let result = [];
inputs.forEach((input, i) => {
  result.push({
    index: i,
    tag: input.tagName,
    type: input.type,
    name: input.name,
    placeholder: input.placeholder,
    ariaLabel: input.getAttribute('aria-label'),
    role: input.getAttribute('role')
  });
});
return result.slice(0, 10); // First 10 to avoid too much data

// Fill the To field (index 5)
const toField = document.querySelector('input[aria-label="To recipients"]');
if (toField) {
  toField.value = 'namor5772@gmail.com';
  toField.dispatchEvent(new Event('input', { bubbles: true }));
  toField.dispatchEvent(new Event('change', { bubbles: true }));
}

// Fill the Subject field
const subjectField = document.querySelector('input[name="subjectbox"]');
if (subjectField) {
  subjectField.value = 'Automation Text';
  subjectField.dispatchEvent(new Event('input', { bubbles: true }));
  subjectField.dispatchEvent(new Event('change', { bubbles: true }));
}

return 'Fields filled';

// Find the compose body - it's usually a div with contenteditable
const bodyField = document.querySelector('div[aria-label="Message Body"]');
if (bodyField) {
  const poem = `With clicks and fills, the browser sings,
A symphony of automated things.
Through pages vast, it swiftly flows,
Finding data wherever it goes.

Forms are filled with practiced ease,
Navigation smooth as ocean breeze.
A helpful hand in digital space,
Making tasks run at faster pace.`;

  bodyField.innerHTML = poem;
  bodyField.dispatchEvent(new Event('input', { bubbles: true }));
  return 'Poem added to body';
}
return 'Body field not found';

// Find the compose body
const bodyField = document.querySelector('div[aria-label="Message Body"]');
if (bodyField) {
  const poem = `With clicks and fills, the browser sings,
A symphony of automated things.
Through pages vast, it swiftly flows,
Finding data wherever it goes.

Forms are filled with practiced ease,
Navigation smooth as ocean breeze.
A helpful hand in digital space,
Making tasks run at faster pace.`;

  bodyField.textContent = poem;
  bodyField.dispatchEvent(new Event('input', { bubbles: true }));
  return 'Poem added to body';
}
return 'Body field not found';

// Press escape to close autocomplete and check subject
document.activeElement.blur();
const subjectField = document.querySelector('input[name="subjectbox"]');
return subjectField ? subjectField.value : 'Subject field not found';

// Close any autocomplete by pressing Escape
const event = new KeyboardEvent('keydown', {
  key: 'Escape',
  code: 'Escape',
  keyCode: 27,
  bubbles: true
});
document.dispatchEvent(event);
return 'Escape pressed';

// Find and click the Send button
const sendButton = document.querySelector('button[aria-label*="Send"]') || 
                   Array.from(document.querySelectorAll('button')).find(btn => btn.textContent.trim() === 'Send');
if (sendButton) {
  sendButton.click();
  return 'Send button clicked';
}
return 'Send button not found';

// Find all buttons and divs that might be the send button
const allButtons = Array.from(document.querySelectorAll('button, div[role="button"]'));
const candidates = allButtons.filter(btn => {
  const text = btn.textContent.toLowerCase();
  const ariaLabel = btn.getAttribute('aria-label')?.toLowerCase() || '';
  return text.includes('send') || ariaLabel.includes('send');
}).map(btn => ({
  text: btn.textContent.substring(0, 50),
  ariaLabel: btn.getAttribute('aria-label'),
  className: btn.className.substring(0, 100)
}));
return candidates;

