// Click the Send button
const sendButton = document.querySelector('button[aria-label*="Send"][aria-label*="Ctrl-Enter"]');
if (sendButton) {
  sendButton.click();
  return 'Email sent!';
}
return 'Send button not found';

// Use the class we found
const sendButton = document.querySelector('div[aria-label*="Send"][role="button"]');
if (sendButton) {
  sendButton.click();
  return 'Email sent via div!';
}

// Try as regular button
const sendBtn2 = Array.from(document.querySelectorAll('div[role="button"]')).find(btn => 
  btn.getAttribute('aria-label')?.includes('Send') && btn.getAttribute('aria-label')?.includes('Ctrl')
);
if (sendBtn2) {
  sendBtn2.click();
  return 'Email sent via array search!';
}
return 'Still not found';

const emailField = document.querySelector('input[type="email"]');
if (emailField) {
  emailField.value = 'namor5772@gmail.com';
  emailField.dispatchEvent(new Event('input', { bubbles: true }));
  emailField.dispatchEvent(new Event('change', { bubbles: true }));
  return 'Email filled';
}
return 'Email field not found';

const nextButton = Array.from(document.querySelectorAll('button, div[role="button"]')).find(btn => 
  btn.textContent.trim() === 'Next'
);
if (nextButton) {
  nextButton.click();
  return 'Next button clicked';
}
return 'Next button not found';

// Find the email row with "Roman Groblicki" and "Automation Text"
const emailRows = Array.from(document.querySelectorAll('tr'));
const targetRow = emailRows.find(row => 
  row.textContent.includes('Roman Groblicki') && 
  row.textContent.includes('Automation Text')
);
if (targetRow) {
  targetRow.click();
  return 'Email clicked';
}
return 'Email not found';

// Check if there's a popup or if we need to handle the OAuth flow differently
return {
  url: window.location.href,
  title: document.title,
  popups: window.opener ? 'Has opener' : 'No opener'
};
