    background-image: radial-gradient(1px 1px at 10% 20%, rgba(255,255,255,0.8), transparent), radial-gradient(1px 1px at 30% 60%, rgba(255,255,255,0.6), transparent), radial-gradient(2px 2px at 50% 10%, rgba(255,215,0,0.5), transparent), radial-gradient(1px 1px at 70% 40%, rgba(255,255,255,0.7), transparent), radial-gradient(1px 1px at 90% 80%, rgba(255,255,255,0.5), transparent), radial-gradient(2px 2px at 15% 85%, rgba(255,215,0,0.4), transparent), radial-gradient(1px 1px at 45% 45%, rgba(255,255,255,0.9), transparent), radial-gradient(1px 1px at 80% 15%, rgba(255,255,255,0.6), transparent); }
