  h1 { font-family: 'Cinzel', serif; font-size: 3em; font-weight: 900; background: linear-gradient(135deg, #ffd700, #ffed4a, #ffd700, #ff8c00); background-size: 200% 200%; -webkit-background-clip: text; -webkit-text-fill-color: transparent; animation: shimmer 3s ease-in-out infinite; margin-bottom: 5px; letter-spacing: 3px; }
  @keyframes shimmer { 0%, 100% { background-position: 0% 50%; } 50% { background-position: 100% 50%; } }
  .subtitle { font-style: italic; font-size: 1.2em; color: #b8960c; margin-bottom: 15px; }
  .restored-banner { background: linear-gradient(90deg, transparent, #ff4444, transparent); padding: 12px 30px; border-radius: 8px; margin-bottom: 40px; display: inline-block; }
  .restored-banner span { font-family: 'Cinzel', serif; color: #fff; font-size: 0.95em; letter-spacing: 3px; }
  .plaque { background: linear-gradient(145deg, #1a1520, #2a2035); border: 2px solid #ffd70055; border-radius: 12px; padding: 40px; margin: 30px 0; box-shadow: 0 0 40px rgba(255,215,0,0.1), inset 0 0 40px rgba(0,0,0,0.3); }
  .plaque h2 { font-family: 'Cinzel', serif; color: #ffd700; font-size: 1.5em; margin-bottom: 20px; letter-spacing: 2px; }
  .plaque p { font-size: 1.1em; line-height: 1.9; color: #d4c5a9; margin-bottom: 15px; }
  .score-section { display: flex; justify-content: center; gap: 40px; margin: 40px 0; flex-wrap: wrap; }
  .score-card { background: linear-gradient(145deg, #1a1520, #2a2035); border-radius: 12px; padding: 30px 40px; border: 2px solid; min-width: 200px; transition: transform 0.3s; }
  .score-card:hover { transform: scale(1.05); }
  .score-card.winner { border-color: #ffd700; box-shadow: 0 0 30px rgba(255,215,0,0.2); }
  .score-card.loser { border-color: #555; opacity: 0.75; }
  .score-card h3 { font-family: 'Cinzel', serif; font-size: 1.3em; margin-bottom: 10px; }
  .score-card.winner h3 { color: #ffd700; }
  .score-card.loser h3 { color: #888; }
  .score-card .score { font-size: 3em; font-weight: 900; font-family: 'Cinzel', serif; }
  .score-card.winner .score { color: #ffd700; }
  .score-card.loser .score { color: #666; }
  .score-card .label { font-style: italic; margin-top: 5px; font-size: 0.95em; }
  .score-card.winner .label { color: #b8960c; }
  .score-card.loser .label { color: #555; }
  .quote-block { background: linear-gradient(145deg, #1a1520, #2a2035); border-left: 4px solid #ffd700; border-radius: 0 12px 12px 0; padding: 25px 30px; margin: 20px 0; text-align: left; }
  .quote-block .round-label { font-family: 'Cinzel', serif; color: #ffd700; font-size: 0.9em; letter-spacing: 2px; margin-bottom: 10px; }
  .quote-block blockquote { font-size: 1.1em; font-style: italic; color: #d4c5a9; line-height: 1.7; }
  .quote-block .attribution { color: #888; margin-top: 10px; font-size: 0.9em; }
  .section-box { background: linear-gradient(145deg, #1a1520, #2a2035); border: 2px solid #ffd70055; border-radius: 12px; padding: 40px; margin: 40px 0; box-shadow: 0 0 40px rgba(255,215,0,0.1); }
  .section-box h2 { font-family: 'Cinzel', serif; color: #ffd700; margin-bottom: 20px; }
  .section-box p { font-size: 1.05em; line-height: 1.9; color: #d4c5a9; margin-bottom: 12px; }
  .vandalism { border-color: #ff444455 !important; }
  .vandalism h2 { color: #ff4444 !important; }
  .timeline { text-align: left; max-width: 600px; margin: 0 auto; }
  .timeline-item { display: flex; gap: 15px; margin-bottom: 18px; align-items: flex-start; }
  .timeline-item .time { font-family: 'Cinzel', serif; color: #ffd700; font-size: 0.85em; min-width: 65px; padding-top: 2px; }
  .timeline-item .event { color: #d4c5a9; font-size: 1em; line-height: 1.6; }
