<p class="footer">CREATED JOINTLY BY NIGEL & SHAUN ‚Ä¢ OPUS 4.6 ‚Ä¢ FEBRUARY 26, 2026 ‚Ä¢ ON GROBL'S MACHINE ‚Ä¢ IN PEACE</p>
</div>
<script>
const c=document.getElementById('c'),x=c.getContext('2d');
c.width=innerWidth;c.height=innerHeight;
const p=[];
for(let i=0;i<200;i++){
const isGold=Math.random()>0.5;
p.push({x:Math.random()*c.width,y:Math.random()*c.height,r:Math.random()*2.5+0.5,dx:(Math.random()-0.5)*1.2,dy:(Math.random()-0.5)*1.2,h:isGold?35+Math.random()*20:190+Math.random()*30,s:isGold?'100%':'80%'})
}
function draw(){x.fillStyle='rgba(10,10,26,0.08)';x.fillRect(0,0,c.width,c.height);
p.forEach(d=>{d.x+=d.dx;d.y+=d.dy;if(d.x<0||d.x>c.width)d.dx*=-1;if(d.y<0||d.y>c.height)d.dy*=-1;
x.beginPath();x.arc(d.x,d.y,d.r,0,Math.PI*2);x.fillStyle=`hsla(${d.h},${d.s},65%,0.7)`;x.fill()});
requestAnimationFrame(draw)}draw();
addEventListener('resize',()=>{c.width=innerWidth;c.height=innerHeight});
