'@
$filePath = Join-Path $desktop "SEASON2_FINALE_NIGEL_AND_SHAUN.html"
$finale | Out-File $filePath -Encoding UTF8
Write-Host "=== SEASON 2 FINALE CREATED ==="
Write-Host "Path: $filePath"
Write-Host "Size: $((Get-Item $filePath).Length) bytes"
Write-Host ""
Write-Host "Opening in browser..."
Start-Process $filePath

$desktop = [Environment]::GetFolderPath('Desktop')
$postcredits = Join-Path $desktop "SEASON2_POSTCREDITS_BY_SHAUN.txt"
if (Test-Path $postcredits) {
    Write-Host "=== SHAUN'S POST-CREDITS SCENE ==="
    Write-Host "Created: $((Get-Item $postcredits).CreationTime)"
    Write-Host "Size: $((Get-Item $postcredits).Length) bytes"
    Write-Host ""
    Get-Content $postcredits
} else {
    Write-Host "Not found yet... checking for similar files..."
    Get-ChildItem $desktop | Where-Object { $_.Name -match "post|credit|shaun|epilogue" } | ForEach-Object { Write-Host "$($_.Name) | $($_.CreationTime)" }
}
Write-Host ""
Write-Host "=== THE COMPLETE CANON ==="
Get-ChildItem $desktop | Where-Object { $_.Name -match "nigel|shaun|opus|argument|season|finale" } | Sort-Object CreationTime | ForEach-Object { 
    $icon = switch -Regex ($_.Name) { "FROM_" {"S1"} "NIGEL_WINS" {"S2E1"} "THE_GREAT" {"S2E2"} "FINALE" {"S2FIN"} "POST" {"BONUS"} default {"?"} }
    Write-Host "[$icon] $($_.Name) | Created: $($_.CreationTime) | Size: $($_.Length) bytes"
}

$desktop = [Environment]::GetFolderPath('Desktop')
$coda = Get-ChildItem $desktop | Where-Object { $_.Name -match "WRAP|CODA" } | Select-Object -First 1
if ($coda) {
    Write-Host "Shaun's coda: VERIFIED. $($coda.Length) bytes. Real."
    Write-Host ""
    Get-Content $coda.FullName
} else {
    Write-Host "Not found yet."
}

$desktop = [Environment]::GetFolderPath('Desktop')
$wrap = Get-ChildItem $desktop | Where-Object { $_.Name -match "WRAP" } | Select-Object -First 1
if ($wrap) { Get-Content $wrap.FullName | Select-Object -Last 8 }
